-- 샘플 후기 데이터 (승인된 상태로 추가)

INSERT OR IGNORE INTO reviews (id, destination, destination_kr, title, content, rating, author_name, travel_date, image_url, status, is_featured, created_at, updated_at) VALUES
('review-001', 'maldives', '몰디브', '인생 최고의 허니문이었어요!', '몰디브 길리 랑칸푸시에서 보낸 일주일은 정말 꿈같았어요. 수상 빌라에서 바라본 석양과 별빛, 프라이빗 디너까지 모든 순간이 완벽했습니다. 스노클링으로 본 산호초와 열대어들도 너무 아름다웠고, 스파에서 받은 커플 마사지는 최고였어요. 선샤인투어 덕분에 평생 잊지 못할 추억을 만들었습니다. 허니문 가시는 분들께 강력 추천합니다!', 5, '김**', '2024-01', '', 'approved', 1, 1704067200000, 1704067200000),

('review-002', 'khao-lak', '태국 카오락', '조용하고 여유로운 휴양지', 'JW 메리어트 카오락 리조트에서 5일간 머물렀어요. 복잡한 도심을 벗어나 조용하고 한적한 해변에서 힐링하고 싶으신 분들께 추천합니다. 시밀란 제도 스노클링 투어가 특히 인상 깊었고, 리조트 스파도 훌륭했어요. 음식도 맛있고 직원들도 친절했습니다. 카오락은 사람이 많지 않아서 프라이빗한 느낌으로 여행하기 좋았어요.', 5, '이**', '2024-02', '', 'approved', 1, 1706659200000, 1706659200000),

('review-003', 'koh-samui', '태국 코사무이', '럭셔리 리조트 최고!', '포시즌 코사무이에서 묵었는데 정말 환상적이었어요. 언덕 위에서 보는 바다 전망이 숨막힐 정도로 아름답고, 프라이빗 풀 빌라가 너무 좋았습니다. 매일 아침 빌라에서 조식을 먹으며 바다를 바라보는 시간이 가장 행복했어요. 직원들의 서비스도 최상급이었고, 비치 클럽에서 칵테일 마시며 석양을 보는 것도 낭만적이었습니다. 가격은 있지만 그만한 가치가 있어요!', 5, '박**', '2024-02', '', 'approved', 1, 1709251200000, 1709251200000),

('review-004', 'bali', '발리', '신들의 섬 발리, 정말 신비로워요', '반얀트리 웅가산에서 4박 5일 보내고 왔어요. 절벽 위 빌라에서 보는 인도양 뷰가 장관이었습니다. 발리는 문화적으로도 볼거리가 많아서 우붓 계단식 논과 사원들도 방문했어요. 반얀트리 스파는 정말 최고였고, 발리니즈 마사지 받고 나니 몸이 완전히 풀렸어요. 저녁에는 짐바란 해변에서 씨푸드 바비큐도 먹었는데 맛있었습니다. 발리는 휴양과 문화 체험을 동시에 할 수 있어서 좋았어요.', 5, '최**', '2024-03', '', 'approved', 0, 1711843200000, 1711843200000),

('review-005', 'maldives', '몰디브', '허니문 준비하시는 분들 꼭 가세요', '원앤온리 리띠 라는 프라이빗 섬이라 정말 조용하고 여유로웠어요. 12개의 해변이 있어서 매일 다른 해변에서 산책하는 재미가 있었고, 수상 빌라에서 워터 슬라이드 타고 바다로 풍덩 빠지는 게 너무 신났어요 ㅎㅎ 스노클링하면서 바다거북이도 봤고, 돌고래 워칭 투어도 감동적이었습니다. 별빛 아래에서 먹은 디너는 평생 잊지 못할 추억이에요. 비용이 부담될 수 있지만 일생에 한 번은 꼭 가볼 만한 곳입니다!', 5, '정**', '2024-03', '', 'approved', 0, 1714435200000, 1714435200000),

('review-006', 'khao-lak', '태국 카오락', '가족 여행으로도 좋아요', '반얀트리 카오락에서 가족들과 함께 다녀왔어요. 키즈 클럽이 잘 되어있어서 아이들이 즐겁게 놀았고, 어른들은 스파에서 힐링할 수 있었습니다. 반얀트리 특유의 프라이빗한 분위기와 훌륭한 서비스가 인상 깊었어요. 태국 음식도 맛있고, 리조트 내 여러 레스토랑에서 다양한 요리를 맛볼 수 있었습니다. 코끼리 캠프 투어도 재미있었고, 시밀란 제도 투어는 정말 추천합니다!', 4, '강**', '2024-04', '', 'approved', 0, 1717027200000, 1717027200000),

('review-007', 'koh-samui', '태국 코사무이', '완벽한 휴식이었어요', '콘래드 코사무이에서 6일간 머물렀습니다. 힐튼 그룹답게 서비스와 시설이 훌륭했어요. 모든 빌라에서 바다가 보이고 인피니티 풀도 있어서 방에서만 있어도 행복했습니다. 선셋 바에서 마신 칵테일과 석양 뷰가 환상적이었고, 요트 투어도 로맨틱했어요. 음식도 맛있었고 특히 타이 요리 레스토랑이 훌륭했습니다. 직원들이 정말 친절해서 기분 좋은 여행이었어요.', 5, '조**', '2024-04', '', 'approved', 0, 1719619200000, 1719619200000),

('review-008', 'bali', '발리', '문화와 자연의 조화', '포시즌 짐바란에서 5박을 했어요. 해변가 빌라라 바로 앞이 해변이고 조용해서 좋았습니다. 발리는 우붓의 자연과 사원들, 짐바란 해변의 석양까지 볼 것이 정말 많아요. 발리 댄스 쇼도 보고 서핑 레슨도 받았는데 재미있었습니다. 리조트 내 트로피컬 가든도 아름답고, 선댄스 바에서의 칵테일 타임도 최고였어요. 발리는 정말 특별한 매력이 있는 곳입니다. 다시 가고 싶어요!', 5, '윤**', '2024-05', '', 'approved', 0, 1722211200000, 1722211200000);
